import React, { Component } from "react";

class Click extends Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0
    };
  }

  update = () => {
    this.setState({ count: this.state.count + 1 });
  };

  render() {
    const { count } = this.state;
    return (
      <div>
        <button onClick={this.update}>
          clicked 1 item {count}
        </button>
      </div>
    );
  }
}

export default Click;
